    document.addEventListener(\'DOMContentLoaded\', function(event) {
        var isMobile = {
            Android: function() {
                return /Android/i.test(navigator.userAgent);
            },
            BlackBerry: function() {
                return /BlackBerry|BB10/i.test(navigator.userAgent);
            },
            iOS: function() {
                return /iPhone|iPad|iPod/i.test(navigator.userAgent);
            },
            WindowsPhone: function() {
                return /windows phone/i.test(navigator.userAgent);
            }
        };

        var $element;

        if (isMobile.Android()) {
            $element = document.querySelector(\'.only-ios, .only-bb, .only-wp\');
        } else if(isMobile.iOS()){
            $element = document.querySelector(\'.only-android, .only-bb, .only-wp\');
        } else if(isMobile.BlackBerry()){
            $element = document.querySelector(\'.only-android, .only-ios, .only-wp\');
        } else if(isMobile.WindowsPhone()){
            $element = document.querySelector(\'.only-android, .only-ios, .only-bb\');
        } else {
            $element = document.querySelector(\'.only-mobile\');
        }

        if($element) $element.style.display = \'none\';
    });
